pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";

/*
Test accounts
1.  0xCA35b7d915458EF540aDe6068dFe2F44E8fa733c
2.  0x14723A09ACff6D2A60DcdF7aA4AFf308FDDC160C
3.  0x4B0897b0513fdC7C541B6d9D7E929C4e5364D2dB
4.  0x583031D1113aD414F02576BD6afaBfb302140225

*/


contract Citizen {
    using SafeMath for uint256;

    event adminAdded(address _admin_address);
    event adminRemoved(address _admin_address);
    event citizenAdded(string _username, string _ref_by, address _wallet_address);
    event refUpdated(string _ref_by, string _username);
    event walletUpdated(address _wallet_address, string _username);


    modifier onlyAdmin() {
        require(isAdmin(msg.sender), "Only admin can do");
        _;
    }

    struct Profile {
        address wallet_address;
        string ref_by;
        string[] refs_to;
        bool exists;
        uint256 total_withdrawed;
        uint256 total_refs;
        uint256 total_sales;
        uint256 total_ref_income;
    }


    mapping (string => Profile) public citizen;
    mapping (address => string[]) public wallet_usernames;
    mapping (string => mapping (string => uint256)) public map_index;
    address public foundation;
    bool internal set_foundation_address;
    bytes32 internal empty_string = keccak256("");
    address private root_admin;
    address[] public admin_address;


    function appendStr(string memory a, string memory b, string memory c, string memory d, string memory e)
        internal pure returns (string memory) {
        return string(abi.encodePacked(a, b, c, d, e));
    }
    function toString(address x) internal pure returns (string memory) {
        bytes memory b = new bytes(20);
        for (uint i = 0; i < 20; i++)
            b[i] = byte(uint8(uint(x) / (2**(8*(19 - i)))));
        return string(b);
    }

    function validUsername(string memory _username) internal pure returns(bool) {
        uint256 len = bytes(_username).length;
        if ((len < 4) || (len > 18)) return false; // Only 4 - 18 letters
        if (uint(uint8(bytes(_username)[len-1])) == 32) return false; // Not ending with space
        return uint(uint8(bytes(_username)[0])) != 48; // Not beginning with 0
    }


    function getOwner() internal view returns (address) {
        return root_admin;
    }

    /* PUBLIC FUNCTIONS */

    function isAdmin(address _admin_address) public view returns (bool) {
        uint admin_count = admin_address.length;
        bool _found = false;
        for (uint i = 0; i<admin_count; i++) {
            if(admin_address[i]==_admin_address){
                _found = true;
                break;
            }
        }
        return _found;
    }

    constructor () public {
        root_admin = msg.sender;
        foundation = msg.sender; // Contract owner will hold foundation's funds until Foundation is established
        admin_address.push(root_admin);
        citizen["foundation"].wallet_address = foundation;
        citizen["foundation"].ref_by = "foundation";
        citizen["foundation"].exists = true;
    }

    function setFoundation(address _foundation) public {
        require(root_admin == msg.sender, "Only root admin can set the Foundation's address");
        require(!set_foundation_address, "POC Token Address can be updated only 1 time");
        set_foundation_address = true;
        foundation = _foundation;
    }

    function addAdmin(address _admin_address) public onlyAdmin() {
        if (!isAdmin(_admin_address)) {
            admin_address.push(_admin_address);
            emit adminAdded(_admin_address);
        }
    }

    function removeAdmin(address _admin_address) public onlyAdmin() {
        uint admin_count = admin_address.length;
        for (uint i = 0; i < admin_count; i++) {
            if(admin_address[i]==_admin_address){
                if (admin_address[i] != root_admin) {
                    delete admin_address[i];
                    emit adminRemoved(_admin_address);
                }
                break;
            }
        }
    }

    function addCitizen(string memory _username, string memory _ref_by, address _wallet_address) public onlyAdmin() returns (string memory) {
        require(!citizen[_username].exists, "Username already exists");
        //Only 4 - 18 letters a-zA-Z.- without having space @ the end and 0 @ the beginning
        require(validUsername(_username), "Username does not valid");
        if (keccak256(abi.encodePacked(_ref_by)) == empty_string) _ref_by = "foundation";
        citizen[_username].ref_by = _ref_by;
        citizen[_username].wallet_address = _wallet_address;
        citizen[_username].exists = true;

        map_index[_ref_by][_username] = citizen[_ref_by].refs_to.length;
        citizen[_ref_by].refs_to.push(_username);

        map_index[toString(_wallet_address)][_username] = wallet_usernames[_wallet_address].length;
        wallet_usernames[_wallet_address].push(_username);
        emit citizenAdded(_username, _ref_by, _wallet_address);
        return "done";
    }

    function updateRef(string memory _ref_by, string memory _username) public onlyAdmin() {
        // Remove current index Map of refs_to
        delete citizen[citizen[_username].ref_by].refs_to[map_index[citizen[_username].ref_by][_username]];
        delete map_index[citizen[_username].ref_by][_username];

        //Update citizen
        citizen[_username].ref_by = _ref_by;
        //Add refs_to

        map_index[_ref_by][_username] = citizen[_ref_by].refs_to.length;
        citizen[_ref_by].refs_to.push(_username);
        emit refUpdated(_ref_by, _username);
    }

    function updateWallet(address _wallet_address, string memory _username) public onlyAdmin() returns (string memory) {
        // Remove current index Map of Wallet
        delete wallet_usernames[citizen[_username].wallet_address][map_index[toString(citizen[_username].wallet_address)][_username]];
        delete map_index[toString(citizen[_username].wallet_address)][_username];

        //Update citizen
        citizen[_username].wallet_address = _wallet_address;

        //Add wallet to username
        map_index[toString(_wallet_address)][_username] = wallet_usernames[_wallet_address].length;
        wallet_usernames[_wallet_address].push(_username);
        emit walletUpdated(_wallet_address, _username);
        return "done";
    }

    function getCitizenWallet(string memory _username) public view returns (address) {
        return citizen[_username].wallet_address;
    }

    function getCitizenRefBy(string memory _username) public view returns (string memory) {
        return citizen[_username].ref_by;
    }

    function getRefsTo(string memory _username) public view returns (string[] memory) {
        return citizen[_username].refs_to;
    }

    function getUsernamesFromWallet(address _wallet_address) public view returns (string[] memory) {
        return wallet_usernames[_wallet_address];
    }

    function getFoundationWallet() public view returns (address) {
        return foundation;
    }
}
