pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

interface CitizenInterface {
    /*----------  ADMINISTRATOR ONLY FUNCTIONS  ----------*/
    function addCitizen(string calldata _username, string calldata _ref_by, address _wallet_address) external returns (string memory);

    /*----------  WRITABLE FUNCTIONS  ----------*/
    function updateRef(string calldata _ref_by, string calldata _username) external;
    function updateWallet(address _wallet_address, string calldata _username) external returns (string memory);

    /*----------  READ-ONLY FUNCTIONS  ----------*/
    function getCitizenWallet(string calldata _username) external view returns (address);
    function getCitizenRefBy(string calldata _username) external view returns (string memory);
    function getUsernameFromAlias(string calldata _alias) external view returns (string memory);
    function isAlias(string calldata _alias) external view returns (bool);

    function getRefsTo(string calldata _username) external view returns (string[] memory);
    function getUsernameFromWallet(address _wallet_address) external view returns (string memory);
    function getFoundationWallet() external view returns (address);

    event adminAdded(address _admin_address);
    event adminRemoved(address _admin_address);
    event citizenAdded(string _username, string _ref_by, address _wallet_address);
    event refUpdated(string _ref_by, string _username);
    event walletUpdated(address _wallet_address, string _username);
    event aliasDeleted(string _alias);
    event aliasAdded(string _username, string _alias);
}
