pragma solidity ^0.5.0;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "./interfaces/PocTokenInterface.sol";
import "./interfaces/CitizenInterface.sol";
import "./interfaces/DateTimeInterface.sol";

contract POC_Pool {
    using SafeMath for uint256;
    event transactionAdded(string _uid, string _username, string _ref_by, uint256 _amount, string _merchant, string _subid, uint256 _release, uint256[10] _ref_rates);
    event transactionRevoked(string _uid);
    event transactionDistributed(string _uid);
    event migrationSet(uint256 _migration_lock);
    event tokenWithdrawed(address _wallet_address);

    modifier onlyAdmin() {
        require(root_admin == msg.sender, "Only root admin can do");
        _;
    }

    address private root_admin;
    enum txstatus { ACTIVE, DISTRIBUTED, REVOKED } // ACTIVE = 0, DISTRIBUTED = 1, REVOKED = 2


    struct Transaction {
        address issuer;
        string username;
        string aliases;
        string ref_by;
        string merchant;
        string subid;
        uint256 amount;
        uint256 release;
        txstatus status;
        uint256[10] ref_rates; 
    }

    mapping (string => bool) public isUid;
    mapping (string => Transaction) public transaction;
    uint256 ONE_MONTH = 2592000;
    uint256 mint_rate = 9000; // over 10000
    PocTokenInterface private poc_token;
    CitizenInterface private citizen;
    DateTimeInterface private datetime;
    bool private set_poc_token_address;

    // Declaration for stats variables
    uint24 this_month;
    uint24 last_month;
    uint256 first_of_next_month;
    mapping (address => uint256) public balance;
    mapping (uint24 => mapping (string => mapping (string => uint256))) public direct_revenue;

    /*
        Declaration for Pool upgrade
        In case we need to upgrade Pool, foundation has ability to stop this pool,
        within 30 days, citizens can withdraw their balance,
        after that foundation will be able to withdraw the rest
    */
    uint256 migration_lock = 253370764800; // 01-01-9999 very very far from now

    constructor (address _datetime_contract) public {
        root_admin = msg.sender;
        datetime = DateTimeInterface(_datetime_contract);
    }

    function setPocTokenAddress(address _poc_token) public onlyAdmin() {
        require(!set_poc_token_address, "POC Token Address can be updated only 1 time");
        set_poc_token_address = true;
        poc_token = PocTokenInterface(_poc_token);
    }

    function setCitizenAddress(address _citizen) public onlyAdmin() {
        citizen = CitizenInterface(_citizen);
    }

    function setMigration() public onlyAdmin() {
        migration_lock = block.timestamp + ONE_MONTH;
        emit migrationSet(migration_lock);
    }

    function setMintRate(uint256 _mint_rate) public onlyAdmin() {
        mint_rate = _mint_rate;
    }

    function migrationWithdraw() public onlyAdmin() returns (bool) {
        require(migration_lock < block.timestamp, "Foundation cannot withdraw until migration lock is over");
        poc_token.transfer(citizen.getFoundationWallet(), poc_token.balanceOf(address(this)));
    }

    function addTransaction(string memory _uid, string memory _username, string memory _ref_by, uint256 _amount, string memory _merchant, string memory _subid, uint256 _release, uint256[10] memory _ref_rates) public returns (string memory) {
        require(isUid[_uid] != true, "Can insert non-active unique UID only");
        require(keccak256(abi.encodePacked(_merchant)) != keccak256("all"), "Merchant name cannot be `all`");
        uint256 _total_ref_rate;
        for (uint i = 0; i<10; i++) {
            require(_ref_rates[i] <= 10000, "Each ref_rate value cannot be greater than 100%");
            _total_ref_rate = _total_ref_rate + _ref_rates[i];
        }
        require(_total_ref_rate <= 10000, "Total of reward level cannot be greater than 100%");
        // Send token from msg.sender to Pool
        poc_token.transferFrom(msg.sender, address(this), _amount);

        isUid[_uid] = true;

        transaction[_uid].issuer = msg.sender;
        transaction[_uid].username = _username;
        transaction[_uid].ref_by = _ref_by;
        transaction[_uid].amount = _amount;
        transaction[_uid].merchant = _merchant;
        transaction[_uid].subid = _subid;
        transaction[_uid].release = _release; // 90s of time variation is not really important
        transaction[_uid].status = txstatus.ACTIVE;
        transaction[_uid].ref_rates = _ref_rates;

        // Check if it is ref by an alias
        if (citizen.isAlias(_ref_by)) {
            transaction[_uid].aliases = transaction[_uid].ref_by;
            transaction[_uid].ref_by = citizen.getUsernameFromAlias(_ref_by);
        }

        emit transactionAdded(_uid, _username, _ref_by, _amount, _merchant, _subid, _release, _ref_rates);
        return "done";
    }

    function revokeTransaction(string memory _uid) public returns (string memory) {
        require(transaction[_uid].status == txstatus.ACTIVE, "Only ACTIVE transaction can be revoked");
        require(transaction[_uid].release > block.timestamp, "Cannot revoke this transaction because time is over");
        require(transaction[_uid].issuer == msg.sender,"Only the issuer of this transaction can revoke it");
        transaction[_uid].status = txstatus.REVOKED;

        // Refund to Issuer
        // poc_token.transfer(transaction[_uid].issuer, transaction[_uid].amount);
        balance[transaction[_uid].issuer] += transaction[_uid].amount;
        delete isUid[_uid];
        emit transactionRevoked(_uid);
        return "done";
    }

    function distributeTransaction(string memory _uid) public {
        require(transaction[_uid].status == txstatus.ACTIVE, "Only ACTIVE transaction can be distributed");
        require(block.timestamp >= transaction[_uid].release, "Transaction is not releasable");
        // Update transaction's status
        transaction[_uid].status = txstatus.DISTRIBUTED;
        // Check if we are on the new month
        if (first_of_next_month < block.timestamp) {
            first_of_next_month = datetime.firstOfNextMonth();
            this_month = datetime.thisMonth();
            last_month = datetime.lastMonth();
        }

        // Add direct_revenue
        string memory _current_ref_by;

        _current_ref_by = transaction[_uid].ref_by;
        direct_revenue[this_month][transaction[_uid].merchant][_current_ref_by] += transaction[_uid].amount;
        direct_revenue[this_month]["all"][_current_ref_by] += transaction[_uid].amount;

        // Burn all tokens of this transaction to 0xdead
        poc_token.transfer(address(0xdead), transaction[_uid].amount);

        // Mint mint_rate / 10000 tokens to systems by referral levels

        for (uint i = 0; i<10; i++) {
            uint256 _to_distribute = transaction[_uid].amount.mul(mint_rate).div(10000).mul(transaction[_uid].ref_rates[i]).div(10000);
            poc_token.poolMint(_to_distribute);
            balance[citizen.getCitizenWallet(_current_ref_by)] += _to_distribute;
            _current_ref_by = citizen.getCitizenRefBy(_current_ref_by);
        }
        emit transactionDistributed(_uid);
    }

    function tokenWithdraw() public {
        require(balance[msg.sender] < poc_token.balanceOf(address(this)), "Pool doesnt have enough token for withdraw ???");
        uint256 _withdraw_amount = balance[msg.sender];
        balance[msg.sender] = 0;
        poc_token.transfer(msg.sender, _withdraw_amount);
        emit tokenWithdrawed(msg.sender);
    }

    function balanceOf(address _wallet_address) public view returns (uint256) {
        return balance[_wallet_address];
    }

    function getCitizenWallet(string memory _username) public view returns (address) {
        return citizen.getCitizenWallet(_username);
    }

    function getCitizenRefBy(string memory _username) public view returns (string memory) {
        return citizen.getCitizenRefBy(_username);
    }

    function getDirectRevenue(string memory _username, string memory _merchant, uint24 _month) public view returns (uint256) {
        return direct_revenue[_month][_merchant][_username];
    }
}
