pragma solidity ^0.5.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Detailed.sol";

contract POC_Token is ERC20, ERC20Detailed {
    event poolMinted(uint256 _amount);

    modifier poolContractOnly() {
        require(msg.sender == poc_pool,"Only POC_Pool contract can mint token");
        _;
    }
    using SafeMath for uint256;
    address poc_pool;
    constructor(address _poc_pool) ERC20Detailed("POC", "POC", 18) public {
        _mint(msg.sender, 2000000 * (10 ** 18));
        poc_pool = _poc_pool;
    }
    function poolMint(uint256 _amount) external poolContractOnly() {
        _mint(msg.sender, _amount);
        emit poolMinted(_amount);
    }
}
